// src/pages/CalendarPage.js
import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const CalendarPage = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Función para calcular todos los miércoles desde una fecha inicial
    const generateWednesdayEvents = (startDate, endDate) => {
      let current = new Date(startDate);
      const wednesdays = [];

      // Avanza hasta el primer miércoles
      while (current.getDay() !== 3) {
        current.setDate(current.getDate() + 1);
      }

      // Agrega un evento cada miércoles hasta la fecha final
      while (current <= endDate) {
        wednesdays.push({
          title: 'Diseño y Desarrollo de Juegos Interactivos I',
          start: new Date(current.setHours(10, 15)), // 9:00 pm
          end: new Date(current.setHours(22, 0)),   // 10:00 pm
          allDay: false,
        });

        // Suma 7 días para pasar al siguiente miércoles
        current.setDate(current.getDate() + 7);
      }

      return wednesdays;
    };

    // Define el rango de fechas
    const startDate = new Date(2024, 8, 23); // 23 de Septiembre 2024
    const endDate = new Date(2024, 11, 31);  // 31 de Diciembre 2024

    // Generar eventos para todos los miércoles
    const wednesdayEvents = generateWednesdayEvents(startDate, endDate);

    // Actualizar el estado con los eventos de los miércoles
    setEvents(wednesdayEvents);
  }, []);

  return (
    <div style={{ height: '500px' }}>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500, margin: '50px' }}
      />
    </div>
  );
};

export default CalendarPage;
