import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination'; 

import { Pagination, Autoplay } from 'swiper/modules'; // Import Autoplay

import './imageSlider.css';

function ImageSlider() {
    return (
        <Swiper
            modules={[Pagination, Autoplay]}  // Add Autoplay module
            spaceBetween={30}
            centeredSlides={true}
            autoplay={{ 
                delay: 3000,  // 3 seconds delay between slides
                disableOnInteraction: false // Keeps autoplay running after interaction
            }}
            pagination={{ clickable: true }}
            loop={true}  // Enables looping of slides
        >
            <SwiperSlide>
                <div className="slide-content">
                    <img src="/img/slid1.png" alt="Slide 1" className="carousel-image" />
                </div>
            </SwiperSlide>

            <SwiperSlide>
                <div className="slide-content">
                    <img src="/img/slide2.png" alt="Slide 2" className="carousel-image" />
                    <div className="slide-text">
                        <h2>Fecha de sorteo: 04 de octubre</h2>
                        <p>
                            Responde la encuesta y participa del sorteo de 1 de 4 Laptops HP Notebook o similares.
                        </p>
                    </div>
                </div>
            </SwiperSlide>

        </Swiper>
    );
}

export default ImageSlider;
